svg.script:
const full = "Greetings, Traveler. I'm Mr. Fadhil";
const svgNS = "http://www.w3.org/2000/svg";
let i = 0;
const interval = 100;
const tspan = document.getElementById("typing");
const cursor = document.querySelector("rect.cursor");
function step() {
  if (i <= full.length) {
    tspan.textContent = full.slice(0, i);
    const bbox = tspan.getBBox();
    cursor.setAttribute("x", bbox.x + bbox.width + 4);
    i++;
    setTimeout(step, interval);
  }
}
step();
